Script.script:
function EK_init_Core()
  return EK or {
    config = {
      plaketten_lesen = true, 
    },
    data = {
      npcs = {},
      diebe = {
        list = {}, -- ordered details
        ids = {}, -- detail to ek ids
        trace = {},
        dones = {},
        temp = "",
      },
      kills = {},
      competitions = {},
      known_kills = 0,
      unknown_kills = 0,
      player_hasnt = -1, 
      competitor_hasnt = -1
    },
    state = {
      loaded = false
    }
  }
 end

EK = EK_init_Core()
