Alias.script:
PLAYER.attr = { con=tonumber(matches[2]), int=tonumber(matches[3]), str=tonumber(matches[4]), dex=tonumber(matches[5]), } save_PLAYER()
main_gui_update_attributes()
MainGUI:info( "Die Attribute wurden wie folgt, gespeichert:
".. f"Ausdauer: {PLAYER.attr.con}
".. f"Intelligenz: {PLAYER.attr.int}
".. f"Kraft: {PLAYER.attr.str}
".. f"Geschick: {PLAYER.attr.dex}")

Script.script:
PLAYER = PLAYER or {} MainGUI = MainGUI or {}
-- Einstellungen fuer Farben Allgemein farben = { vg = { komm = "cyan", ebenen = "red", info = "green", alarm = "white", script = "dark_green" }, hg = { komm = "black", ebenen = "black", info = "black", alarm = "red", script = "black" }, } --- komm: Kommunikation wie teile-mit --- ebenen: Einfaerben der "normalen" Ebenen --- info: Einfaerben von Informationen des Muds (Status Gegner) --- alarm: Alarm-Nachrichten --- script: Nachrichten, die nicht vom Mud, sondern von einem Script stammen. --- Einstellungen fuer Farben Kampfscroll
function msg(type, what) -- setzt VG und HG je nach Typ der Kommunikation local vg = farben.vg[type] local hg = farben.hg[type] if vg and hg then cecho("<" .. vg .. ":" .. hg .. ">" .. what) else echo(what) end end

