Script.script:
WayFinder = WayFinder or MundronClassMethods:new{ _name="WayFinder", _shortname = "WS", _version="1.0.0", _fixed_version={MundronClassMethods="1.1.0"}, files = { profile = { portals = {} }, game = { nodes = {}, edges = {}, gags = {} } }, data = {} }
WayFinder.data.shortendir = WayFinder.data.shortendir or { norden = "n", nordosten = "no", osten = "o", suedosten = "so", sueden = "s", suedwesten = "sw", westen = "w", nordwesten = "nw", } -- PathData.oppositedirection = PathData.oppositedirection or { norden = "sueden", n = "s", nordunten = "nordoben", nordoben = "nordunten", nordosten = "suedwesten", no = "sw", nordostoben = "suedwestunten", nordostunten = "suedwestoben", osten = "westen", o = "w", ostunten = "westoben", ostoben = "westunten", suedosten = "nordwesten", so = "nw", suedostoben = "nordwestunten", suedostunten = "nordwestoben", sueden = "norden", s = "n", suedunten = "nordoben", suedoben = "nordunten", suedwesten = "nordosten", sw = "no", suedwestunten = "nordostoben", suedwestoben = "nordostunten", westen = "osten", w = "o", westunten = "ostoben", westoben = "ostunten", nordwesten = "suedosten", nw = "so", nordwestoben = "suedostunten", nordwestunten = "suedostoben", oben = "unten", ob = "unten", unten = "oben", u = "oben", } -- PathData.aliaseddir = PathData.aliaseddir or { "n", "norden", "nordunten", "nordoben", "no", "nordosten", "nordostunten", "nordostoben", "o", "osten", "ostunten", "ostoben", "so", "suedosten", "suedostunten", "suedostoben", "s", "sueden", "suedunten", "suedoben", "sw", "suedwesten", "suedwestunten", "suedwestoben", "w", "westen", "westunten", "westoben", "nw", "nordwesten", "nordwestunten", "nordwestoben", "ob", "oben", "u", "unten", "raus", }
function WayFinder:init() self.data.dir = {} local nord, sued = {"nord", "n"}, {"sued", "s"} local ost, west = {"ost", "o"}, {"west", "w"} local oben, unten = {"ob", "ob"}, {"unt", "u"} local empty = {"", ""}
local aliases = table.get(self, "data.dir.aliases", {}) local opposite = table.get(self, "data.dir.opposite", {}) local shortendir = table.get(self, "data.dir.shortendir", {})
local get_dirs = function(x, y, z) return f"{y[1]}{x[1]}{z[1]}en", f"{y[2]}{x[2]}{z[2]}" end local save_dirs = function(x_out, x_in, y_out, y_in, z_out, z_in) if x_in == empty and y_in == empty and z_in == empty then return end local long_in, short_in = get_dirs(x_in, y_in, z_in) local long_out, short_out = get_dirs(x_out, y_out, z_out) opposite[long_out] = long_in aliases[long_out] = long_out if z_in == empty or (x_in == empty and y_in == empty) then shortendir[long_out] = short_out opposite[short_out] = short_in aliases[long_out] = short_out aliases[short_out] = short_out end end
local long_out, long_in, short_out, short_in
for x_out, x_in in zip({ost, empty, west}, {west, empty, ost}) do for y_out, y_in in zip({nord, empty, sued}, {sued, empty, nord}) do for z_out, z_in in zip({oben, empty, unten}, {unten, empty, oben}) do save_dirs(x_out, x_in, y_out, y_in, z_out, z_in) end end end aliases["raus"] = "raus"
self:info(f"Got {len(aliases)}/37 aliases, short, long, up, down * 8 directions = 32 + up/down short/long + raus") self:info(f"Got {len(opposite)}/36 opposites all in alias except raus") self:info(f"Got {len(shortendir)}/10 shortendirs, 8 directions + up/down") end

local function transform_entry(entry) if type(entry) == "table" then return json.encode(entry) else return entry end end
function PathCreator:show_last(num) local cpback_len = len(self.cpback) local cpforth_len = len(self.cpforth) num = num or (cpback_len + cpforth_len) local cpback_size = math.min(num, cpback_len) local cpforth_size = math.min(math.max(num - cpback_size, 0), cpforth_len) local result = {} if self.start_id == gmcp.MG.room.info.id then table.insert(result, f("Startknoten mit der Raum-ID {self.start_id}
")) end for i = 1, cpback_size, 1 do local entry = transform_entry(self.cpback[cpback_size - i + 1]) table.insert(result, f("-{i}: {entry}
")) end if num > cpback_len then table.insert(result, f("Zielknoten mit der Raum-ID {self.goal_id}
")) end for i = 1, cpforth_size, 1 do local entry = transform_entry(self.cpforth[cpforth_size - i + 1]) table.insert(result, f("-{cpback_size+i}: {entry}
")) end if cpforth_size == cpforth_len then table.insert(result, f("Startknoten mit der Raum-ID {self.start_id}
")) end iprint(result, "WS") end
function PathCreator:delete_last(num) if self.goback then local cpback_len = len(self.cpback) num = num or cpback_len if num < cpback_len then self.cpback = table.subtable(self.cpback, 1, cpback_len - num) iprint(f("Die letzten {num} Eintraege fuer den Rueckweg wurden geloescht."), "WS") elseif num == cpback_len then self.cpback = {} iprint(f("Alle {num} Eintraege fuer den Rueckweg wurden geloescht."), "WS") else self.cpback = {} iprint( { f("Alle {cpback_len} Eintraege fuer den Rueckweg wurden geloescht. "), "Sollen auch Eintraege zum Zielknoten hin geloescht werden, ", "Musst du das Ziel mit ", {"#wa ziel", "ly"}, " wieder aufloesen und dann die entsprechende Anzahl loeschen.", }, "WS" ) end else local cpforth_len = len(self.cpforth) num = num or cpforth_len if num < cpforth_len then self.cpforth = table.subtable(self.cpforth, 1, cpforth_len - num) iprint(f("Die letzten {num} Eintraege fuer den Hinweg zum Ziel wurden geloescht."), "WS") else self.cpforth = {} iprint(f("Alle {cpforth_len} Eintraege seit dem Startknoten wurden geloescht."), "WS") end end end
